select project.id, project.process_id, project.name, project.number, project.owner_name, project.type, project.owner_id, project.attachment, project.updated_at, project.total_product, project.total_percentage, p.id as product_id, p.user_id, p.display_name, p.product, p.percentage from project inner join product p on project.process_id = p.process_id where year(project.updated_at) = #{year} and ISNULL(project.total_product)=1 and
project.name REGEXP #{query} and
project.type = #{type} and
project.number REGEXP #{number} and
project.owner_id in
#{ownerId}
and project.type in
#{typeId}
and project.created_at between #{startDate} and #{endDate}
and year(project.updated_at) = #{year}
order by project.updated_at desc

